export default function Accordion({ title, imageSrc, children, isOpen, setOpenSection, customStyles }) {
    return (
        <div className="w-full max-w-7xl px-4 sm:px-6 lg:px-8 mx-auto relative mt-4">
            {/* Closed State */}
            {!isOpen && (
                <div 
                    onClick={() => setOpenSection(title)} 
                    className={`p-2 cursor-pointer w-full flex items-center ${customStyles.closed}`}
                >
                    <img src={imageSrc} alt={title} className="p-1 w-1/3 h-auto" />
                    <div className="text-white text-4xl font-semibold">{title}</div>
                </div>
            )}

            {/* Open State */}
            {isOpen && (
                <div className={`p-2 border shadow-md w-full ${customStyles.open}`}>
                    <div className="w-full p-2 flex items-center">
                        <button onClick={() => setOpenSection(null)} className="w-full text-left flex items-center">
                            <img src={imageSrc} alt={title} className="p-1 w-1/6 h-auto" />
                            <span className="text-white text-4xl font-semibold">{title}</span>
                        </button>
                    </div>
                    <div className="p-2">{children}</div>
                </div>
            )}
        </div>
    );
}


