import { useState } from "react";

export default function Accordion({ title, imageSrc, content }) {
    const [open, setOpen] = useState(false);

    return (
        <div className="w-full max-w-7xl px-4 sm:px-6 lg:px-8 mx-auto relative mt-4">
            {/* Closed State */}
            {!open && (
                <div onClick={() => setOpen(true)}
                    className="p-2 cursor-pointer w-full flex items-center bg-cyan-500 hover:bg-cyan-600">
                    <img src={imageSrc} alt={title} className="p-1 w-1/3 h-auto" />
                    <div className="text-white text-4xl font-semibold">{title}</div>
                </div>
            )}

            {/* Open State */}
            {open && (
                <div className="p-2 border border-blue-200 shadow-md w-full bg-red-500">
                    <div className="w-full p-2 flex items-center">
                        <button onClick={() => setOpen(false)} className="w-full text-left flex items-center">
                            <img src={imageSrc} alt={title} className="p-1 w-1/6 h-auto" />
                            <span className="text-white text-4xl font-semibold">{title}</span>
                        </button>
                    </div>
                    <div className="p-2 text-gray-500">{content}</div>
                </div>
            )}
        </div>
    );
}
