import { useState, useRef } from "react";
import TopBar from "./components/TopBar";
import Accordion from "./components/Accordion";

function App() {
    const [openSection, setOpenSection] = useState(null); // Track which section is open

    // Create references for each section
    const artworkRef = useRef(null);
    const performancesRef = useRef(null);

    // Function to handle menu clicks
    const handleMenuClick = (section) => {
        setOpenSection(section); // Open the clicked section

        // Scroll smoothly to the section
        const sectionRef = section === "ARTWORK" ? artworkRef : performancesRef;
        sectionRef.current?.scrollIntoView({ behavior: "smooth", block: "start" });
    };

    return (
        <div className="bg-black min-h-screen">
            <TopBar onMenuClick={handleMenuClick} /> {/* Pass function to TopBar */}

            <div className="pt-24">
                {/* Artwork Section */}
                <div ref={artworkRef} id="artwork">
                    <Accordion 
                        title="ARTWORK" 
                        imageSrc="/images/esofon.jpg" 
                        content={
                            <iframe 
                                width="420" 
                                height="345" 
                                src="https://www.youtube.com/embed/Ffe-7T6yDhU" 
                                title="Artwork Video"
                            ></iframe>
                        }
                        isOpen={openSection === "ARTWORK"} // Control open state
                        setOpenSection={setOpenSection} // Pass function to close others
                    />
                </div>

                {/* Performances Section */}
                <div ref={performancesRef} id="performances">
                    <Accordion 
                        title="PERFORMANCES" 
                        imageSrc="/images/performances.jpeg" 
                        content={<p>This is the Performances section content.</p>}
                        isOpen={openSection === "PERFORMANCES"}
                        setOpenSection={setOpenSection}
                    />
                </div>
            </div>
        </div>
    );
}

export default App;


