import { useState, useRef } from "react";
import TopBar from "./components/TopBar";
import Accordion from "./components/Accordion";
import Artwork from "./components/Sections/Artwork/Artwork";
import Performances from "./components/Sections/Performances/Performances";
import accordionStyles from "./styles/styles";  // Import the styles

function App() {
    const [openSection, setOpenSection] = useState(null);

    const artworkRef = useRef(null);
    const performancesRef = useRef(null);

    const handleMenuClick = (section) => {
        setOpenSection(section);
        const sectionRef = section === "ARTWORK" ? artworkRef : performancesRef;
        sectionRef.current?.scrollIntoView({ behavior: "smooth", block: "start" });
    };

    return (
        <div className="bg-black min-h-screen">
            <TopBar onMenuClick={handleMenuClick} />

            <div className="pt-24">
                {/* Artwork Section */}
                <div ref={artworkRef} id="artwork">
                    <Accordion 
                        title="ARTWORK" 
                        imageSrc="/images/esofon.jpg" 
                        isOpen={openSection === "ARTWORK"} 
                        setOpenSection={setOpenSection}
                        customStyles={accordionStyles.artwork}  // Use imported styles
                    >
                        <Artwork />
                    </Accordion>
                </div>

                {/* Performances Section */}
                <div ref={performancesRef} id="performances">
                    <Accordion 
                        title="PERFORMANCES" 
                        imageSrc="/images/performances.jpeg" 
                        isOpen={openSection === "PERFORMANCES"} 
                        setOpenSection={setOpenSection}
                        customStyles={accordionStyles.performances}  // Use imported styles
                    >
                        <Performances />
                    </Accordion>
                </div>
            </div>
        </div>
    );
}

export default App;
