export default function Quadcopter({ containerClass, textClass, iframeWrapperClass, iframeClass }) {
    return (
        <div className={containerClass}>
            <div className={textClass}>
                <h2 className="text-2xl font-bold pb-8">The Quadcopter Concert</h2>
                <p>
                    Performance from{" "}
                    <a 
                        href="https://tedxcopenhagen.dk/events/mobility/" 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="text-blue-600 underline"
                    >
                        TEDxCopenhagen
                    </a> event, september 17, 2014 with Tim Garbos and Patrick Jarnfelt from <a 
                        href="https://www.copenhagengamecollective.org" 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="text-blue-600 underline"
                    >
                        Copenhagen Game Collective
                    </a>. Patrick later wrote this <a 
                        href="https://www.https://www.copenhagengamecollective.org/2015/11/27/playing-drones-as-instruments/.org" 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="text-blue-600 underline"
                    >
                        blogpost
                    </a>. about the project. The Quadcopter concert is an algorithmic music setup with a direct mapping from the movements of the quadcopters to the output sound. I'm standing in the middle, handling the musical setup, with Tim and Patrick on either side controlling the quadcopters. The piece was first performed at <a 
                        href="https://kuto.dk/click/" 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="text-blue-600 underline"
                    >
                        Click Festival
                    </a>. in may 2014.
                </p>
            </div>

            {/* Responsive iframe wrapper */}
            <div className={`${iframeWrapperClass} relative`} style={{ paddingTop: "56.25%" }}>
                <iframe 
                    className={`${iframeClass} absolute top-0 left-0 w-full h-full`} 
                    src="https://www.youtube.com/embed/g9_yaSGtlQI?si=a-CX0Z1aogXiW0pj" 
                    title="Quadcopter Video" 
                    allowFullScreen
                ></iframe>
            </div>
        </div>
    );
}

