import LinkItem from "../../../LinkItem";

export default function MaerskOpera({ containerClass, textClass, iframeWrapperClass, iframeClass }) {
    const aboutText = [
        "Mærsk"];

    return (

        <div className={containerClass} style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px", alignItems: "center", paddingTop: "40px" }}>
        {/* Text Section */}
        <div className={textClass} style={{ gridColumn: "1", gridRow: "1" }}>
                <h2 className="text-2xl font-bold pb-8">The Mærsk Opera (2012-16) - <LinkItem url="https://tedxcopenhagen.dk/events/mobility/" text="(Score)" /></h2>
                <p>
                An opera in 3 acts, for 26 voices, choir & orchestra. Produced in collaboration with artists group  <LinkItem url="http://totem.menneske.dk" text="SUPERFLEX" /> , who wrote the libretto in collaboration with Nikolai Heltoft. First composed in 2012, where the score was exhibited as a conceptual art piece in <LinkItem url="https://norbergfestival.com" text="Nikolai Kunsthal" /> 
                . Later re-arranged and recorded in 2016, and included in the film <LinkItem url="https://tedxcopenhagen.dk/events/mobility/" text="The Mærsk Opera" /> by SUPERFLEX, premiered at <LinkItem url="https://tedxcopenhagen.dk/events/mobility/" text="CPH DOX" /> 2017.<br key="break" /> A recording of the Opera is released on the Danish label <LinkItem url="https://tedxcopenhagen.dk/events/mobility/" text="DACAPO" />. </p>
            </div>

            {/* Responsive iframe wrapper */}
            <div className={iframeWrapperClass} style={{ gridColumn: "2", gridRow: "1" }}>
                <img src="/images/xlarge_superflex_gaven.jpg" className="w-full h-auto" alt="MaerskPic" />
                {/* <iframe 
                    className={`${iframeClass} absolute top-0 left-0 w-full h-full`} 
                    src="https://www.youtube.com/embed/kZIy3170pgI?si=p2IdV0Wv5S-Lojg4" 
                    title="LegoConcert Video" 
                    allowFullScreen
                ></iframe> */}
            </div>
        </div>

        // <div className="p-4 text-black bg-white bg-opacity-35 text-center">
        //     <p className="mb-4">{aboutText}</p>
        // </div>
    );
}
