import LinkItem from "../../../LinkItem";

export default function Prepositions({ containerClass, textClass, iframeWrapperClass, iframeClass }) {
    return (
        <div className={containerClass} style={{ 
            display: "grid", 
            gridTemplateColumns: "2fr 1fr", // Text takes 2 columns, image takes 1 column
            gap: "20px", 
            alignItems: "center", 
            paddingTop: "50px" 
        }}>

            {/* Image Section (Should appear on the right) */}
            <div className={iframeWrapperClass} style={{ gridColumn: "2 / span 1" }}>
                <img src="/images/ScoreMusic/PrepositionsBillede.jpeg" className="w-full h-auto" alt="Prepositions Image" />
            </div>

            {/* Text Section (Should take 2/3 of the width) */}
            <div className={textClass} style={{ gridColumn: "1 / span 1" }}>
                <h2 className="text-2xl font-bold pb-8 text-left">
                    Prepositions (2015) - <LinkItem url="/files/Prepositions.pdf" text="(Score)" />
                </h2>
                <p>
                    “Prepositions” is a song cycle for soprano and piano, which consists of 9 songs set to texts by the Norwegian author 
                    <LinkItem url="https://aschehoug.no/Demian_Vitanza" text="Demian Vitanza" />. The work dates from the spring of 2015, 
                    composed in close collaboration and dialogue with the author and the soprano Ly Tran, who premiered the work at 
                    <LinkItem url="http://hindsgavlfestival.dk" text="Hindsgavl Festival" /> in 2015, with myself playing the piano part.
                </p>

                {/* Embedded Audio */}
                <iframe 
                    className="w-full h-14 mt-2"
                    src="https://www.youtube.com/embed/57yMZ3GMZD8?si=HjEbYqaYW5S6eBNV&controls=1&showinfo=0&autoplay=0&mute=0"
                    title="PrepositionsA Audio"
                    allow="encrypted media"
                ></iframe>
                <iframe 
                    className="w-full h-14 mt-2"
                    src="https://www.youtube.com/embed/4GJccpuV6nY?si=pxh84rxBi9-ymQD6&controls=1&showinfo=0&autoplay=0&mute=0"
                    title="PrepositionsB Audio"
                    allow="encrypted media"
                ></iframe>
            </div>

        </div>
    );
}


