export default function TopBar({ onMenuClick }) {
    return (
        <nav className="fixed top-0 w-full bg-gray-900 text-white py-4 px-6 flex justify-between">
            <div className="text-2xl font-bold">My Portfolio</div>
            <ul className="flex space-x-6">
                <li>
                    <button onClick={() => onMenuClick("ARTWORK")} className="hover:text-cyan-400">
                        Artwork
                    </button>
                </li>
                <li>
                    <button onClick={() => onMenuClick("PERFORMANCES")} className="hover:text-purple-400">
                        Performances
                    </button>
                </li>
            </ul>
        </nav>
    );
}



